	//==================================== saveLeaveType function for Saving Leave Type details - by Sajitha ================================
	public function saveLeaveType()
	{
		$id				= $this->input->post("hidID");
		
		$data 			= array();  
		$leaveTypeName = $this->input->post('Leave_Type');
	  
